sys_script_include.script:
var RIDEClientAPI = Class.create();
RIDEClientAPI.prototype = Object.extendsObject(global.AbstractAjaxProcessor,
{
	GetApps: function()
	{
		var aApps = GetApps();
		return JSON.stringify(aApps);
	},	

	GetAppData: function()
	{
		var GetStringValue = x_snc_devtools.GetStringValue;
		var strAppId = GetStringValue(this.getParameter('sysparm_app_id'));
		var appdata = GetAppData(strAppId);
		return JSON.stringify(appdata);
	},		

    type: 'RIDEClientAPI'
});
