sys_script_include.script:
function GetAppData(vApp)
{
	var GetAppRecord = x_snc_devtools.GetAppRecord;
	var Clone = x_snc_devtools.Clone;
	var GetCleanAppName = x_snc_devtools.GetCleanAppName;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var ArrayValueExists = x_snc_devtools.ArrayValueExists;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetRecord = x_snc_devtools.GetRecord;

	var grApp = GetAppRecord(vApp);
	if (IsValidRecord(grApp) == false)
	{
		return false;
	}

	var appdata = Clone(grApp);

	appdata.name_clean = GetCleanAppName(appdata.name);

	var aMetaData = [];

	var aIgnoredClasses = [
		'sys_metadata_delete',
		'sys_scope_privilege',
		'sys_restricted_caller_access',
		'sys_dictionary',
		'sys_documentation',
		'sys_user_role_contains',
		'sys_atf_test_suite_test',
		'sys_atf_step',
		'atf_input_variable',
		'sys_security_acl_role',
		'sys_embedded_help_role',
		'sys_ui_policy_action',
		'sys_ui_action_role',
		'ua_table_licensing_config',
		'x_snc_reactor_props',
		'scan_check_suite_check',
		'sys_ws_query_parameter_map',
		'sys_kmf_crypto_caller_policy',
		'sp_instance',
		'sp_row',
		'sp_column',
		'sp_container',
		'm2m_sp_widget_dependency',
		'm2m_sp_dependency_js_include',
		'sys_complex_object',
		'sys_hub_action_output',
		'sys_flow_trigger_auto_script',
		'sys_hub_action_type_definition',
		'sys_hub_action_type_snapshot',
		'sys_hub_flow_input',
		'sys_hub_flow_snapshot',
		'sys_hub_flow_variable',
		'sn_decision_table_decision_condition',
		'sys_aw_list',
		'sys_aw_list_attribute',
		'sys_aw_list_category',
		'sys_aw_module',
		'sys_aw_new_menu_item',
		'sys_aw_renderer',
		'sys_db_view_table',
		'sys_uib_screen_test_values',
		'sys_ui_section'
		];

	var grMetaData = new GlideRecord('sys_metadata');
	grMetaData.orderBy('sys_name');
	grMetaData.addQuery('sys_scope','=',grApp.sys_id);
	for (var nClass = 0; nClass < aIgnoredClasses.length; nClass++)
	{
		grMetaData.addQuery('sys_class_name','!=',aIgnoredClasses[nClass]);
	}
	grMetaData.query();
	while (grMetaData.next())
	{
		var strClassName = grMetaData.getValue('sys_class_name');
		var metadata = Clone(grMetaData);
		delete metadata.sys_scope;
		delete metadata.sys_package; 
		delete metadata.sys_meta;
		delete metadata.sys_update_name;
		var strDisplay = GetStringValue(grMetaData.getDisplayValue());
		if (strDisplay == '')
		{
			strDisplay = grMetaData.getValue('sys_id');
		}
		metadata['_display'] = strDisplay;

		if (strClassName == 'sys_script')
		{
			var grRecord = GetRecord(strClassName,metadata.sys_id);
			metadata['_table'] = GetStringValue(grRecord.collection);
		}
		if (strClassName == 'sys_ui_action' || strClassName == 'sys_ui_policy' )
		{
			var grRecord = GetRecord(strClassName,metadata.sys_id);
			metadata['_table'] = GetStringValue(grRecord.table);
		}		
		aMetaData.push(metadata);
	}
	appdata._metadata = aMetaData; 

	return appdata;
}
