class IDEController extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		var strApp = GetStringValue(this.props.app);
		var strState = 'select';

		if (strApp != '')
		{
			strState = 'edit';
		}

		this.state = {
			state: strState,
			app: strApp
		};
		//this.onClick = this.onClick.bind(this);
	}

	componentDidMount()
	{
	}

	componentWillUnmount()
	{
	}

	/*
	onClick()
	{
		this.setState( {clicked: true} );
		if (typeof this.props.callbackOnClick == 'function')
		{
			this.props.callbackOnClick();
		}
		else if (this.props.text != '')
		{
			DoModalMessage('New Reactor component',this.props.text);
		}
		else
		{
			DoModalMessage('New Reactor component','The button was clicked!');
		}
	}
	*/

	render()
	{
		var strState = GetStringValue(this.state.state);
		var strApp = GetStringValue(this.state.app);



		var components = [];


		if (strState == 'select')
		{
			//components.push(e(WarningMessage,{ text: 'No app selected.'}));
			components.push(e(IDEAppSelectorController,{}));
		}
		else
		{
			components.push(e(InfoMessage,{ text: 'App is "'+strApp+'"'}));
		}
		return e(Fullscreen,
			{
			},
			components);
	}	
}
