x_snc_reactor_component.script:
class IDEController extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		var strApp = GetStringValue(this.props.app);
		var strState = 'select';

		if (strApp != '')
		{
			strState = 'edit';
		}

		this.state = {
			state: strState,
			app: strApp
		};
		this.callbackSelectApp = this.callbackSelectApp.bind(this);
	}

	callbackSelectApp(strAppSysId)
	{
		DoModalMessage('IDE','App has been selected: '+strAppSysId);
		this.setState(
			{
				app: strAppSysId,
				state: 'edit'
			}
		);
	}

	render()
	{
		var strState = GetStringValue(this.state.state);
		var strApp = GetStringValue(this.state.app);

		var components = [];


		if (strState == 'select')
		{
			components.push(e(IDEAppSelectorController,{ callbackSelectApp: this.callbackSelectApp }));
		}
		else
		{
			components.push(e(InfoMessage,{ text: 'App is "'+strApp+'"'}));
		}
		return e(Fullscreen,
			{
			},
			components);
	}	
}
