x_snc_reactor_component.script:
class IDEController extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		var strAppId = GetStringValue(this.props.app_id);
		var strState = 'select';

		if (strAppId != '')
		{
			strState = 'edit';
		}

		this.state = {
			state: strState,
			app_id: strAppId,
			app_data: null
		};
		this.callbackSelectApp = this.callbackSelectApp.bind(this);
		this.onServerRequestGetAppData = this.onServerRequestGetAppData.bind(this);

		this.callbackToolBar = this.callbackToolBar.bind(this);
		this.callbackTreeNavigation = this.callbackTreeNavigation.bind(this);
	}

	callbackSelectApp(strAppSysId)
	{
		if (this.state.app_id == strAppSysId)
		{
			this.setState(
				{
					state: 'edit'
				});
			return;
		}
		this.setState(
			{
				app_data: null,
				app_id: strAppSysId,
				state: 'edit'
			});
	}

	onServerRequestGetAppData(appdata)
	{
		this.setState(
			{
				state: 'edit',
				app_data: appdata
			}
		);
	}

	callbackToolBar(strValue)
	{
		if (strValue == 'app_select')
		{
			this.setState(
				{
					state: 'select'
				});
			return;
		}
		if (strValue == 'file_create')
		{
			DoModalMessage('IDE','TODO: A file is to be created...',function(){});
			return;
		}
		if (strValue == 'repo_link')
		{
			RedirectToUrl(this.state.app_data.x_snc_devtools_repo_url,true);
			return;
		}		
		if (strValue == 'apiref_link')
		{
			RedirectToUrl('https://developer.servicenow.com/dev.do#!/reference/',true);
			return;
		}
		if (strValue == 'fontawesome_link')
		{
			RedirectToUrl('https://fontawesome.com/v4/icons/',true);
			return;
		}		
		if (strValue == 'error')
		{
			DoModalMessage('IDE','Provoking an error...',function(){});
			this.setState(
				{
					state: 'error'
				});			
			return;
		}

	}

	callbackTreeNavigation(strValue)
	{
		DoModalMessage('IDE',strValue,function(){});
	}

	MakeTreeNavigation(appdata)
	{
		var treenavigation = { items: [] };

		var tn_category = { label: 'By Category', items: [] };
		//var tn_table = { label: 'By Table', items: [] };
		var tn_all = { label: 'All', items: [] };

		var aFiles = GetValue(appdata,'_metadata');

		//var itemsByTables = {};
		for (var nFile = 0; nFile < aFiles.length; nFile++)
		{
			var file = aFiles[nFile];

			var strClassName = GetValue(file,'sys_class_name');
			var strSysId = GetValue(file,'sys_id');
			var strName = GetValue(file,'sys_name');

			// All
			tn_all.items.push( { label: strName, value: strClassName+','+strSysId } );

			// By Table
			//itemsByTables = SetValue(strName,strClassName,strSysId);
		}

		/*
		var aClassNames = GetKeys(itemsByTables);
		aClassNames = aClassNames.sort();
		for (var nClassName = 0; nClassName < aClassNames.length; nClassName)
		{
			var strClassName = aClassNames[nClassName];
			var tn_files = { label: strClassName, items: [] };
			var itemsByTable = itemsByTables[strClassName];
			var aSysIds = GetKeys(itemsByTable);
			for (var nSysId = 0; nSysId < aSysIds.length; nSysId++)
			{
				var strSysId = aSysIds[nSysId];
				var strName = GetValue(itemsByTable,strSysId);
				tn_files.items.push( { label: strName, value: strClassName+','+strSysId } );
			}
			tn_table.items.push( tn_files );
		}
		*/



		//treenavigation.items.push( tn_table );
		treenavigation.items.push( tn_all );


		return treenavigation;
	}


	render()
	{
		var strState = GetStringValue(this.state.state);
		var strAppId = GetStringValue(this.state.app_id);
		var appdata = this.state.app_data;

		if (strAppId == '')
		{
			strState = 'select';
		}
		if (strState == 'edit')
		{
			if (IsObject(appdata) == false)
			{
				strState = 'loading';
			}
		}


		if (strState == 'select')
		{
			return e(Fullscreen,{},e(Centered,{},e(IDEAppSelectorController,{ app_id: strAppId, callbackSelectApp: this.callbackSelectApp })));			
		}
		else if (strState == 'loading')
		{
			ServerRequest('x_snc_ide.IDEClientAPI','GetAppData',{ app_id: strAppId },this.onServerRequestGetAppData);
			return e(Fullscreen,{},e(Centered,{},e(Waiting,{ text: 'Loading App...' })));
		}
		else if (strState == 'edit')
		{

			var strAppNameVersion = appdata.name + ' ' + appdata.version;


			var logo = e('div',
				{
					style:
					{
						fontWeight: 'bold',
						fontSize: '16px',
						margin: '5px'
					}
				},
				'ServiceNow IDE - ' + appdata.name_clean);


			var tools = [];
			tools.push({
					text: 'Load App',
					icon: 'folder-open',
					value: 'app_select'
				});
			tools.push({
					text: 'Create Application File',
					icon: 'plus-circle',
					value: 'file_create'
				});
			tools.push({
				text: '',
				icon: 'user-circle',
				value: 'apiref_link'
			});			
			if (IsValidUrl(appdata.x_snc_devtools_repo_url) == true)
			{
				tools.push({
						text: '',
						icon: 'code-fork',
						value: 'repo_link'
					});
			}
			tools.push({
				text: '',
				icon: 'font-awesome',
				value: 'fontawesome_link'
			});
			tools.push({
					text: 'Provoke Error',
					value: 'error'
				});

			var treenavigation = this.MakeTreeNavigation(appdata);

			var strStatus = strAppNameVersion;		
			var status = e('span',{},strStatus);

			var workspace = e(Workspace,{
				logo: logo,
				menu: 'MENU',
				user: 'USER',
				toolbar: e(ToolBar, { tools: tools, callbackOnClick: this.callbackToolBar }),
				treenavigation: e(TreeNavigation, { treenavigation: treenavigation, callbackOnClick: this.callbackTreeNavigation }),
				content: 'CONTENT',
				sidebar: 'SIDEBAR',
				status: status 
			});

			return workspace;
		}
		return e(ErrorMessage,{ text: 'ERROR: INVALID STATE' });
	}	
}
