	render()
	{
		var strScope = GetStringValue(this.state.scope);
		if (strScope == '')
		{
			return e(WarningMessage,{ text: 'No scope selected.'});
		}
		else
		{
			return e(InfoMessage,{ text: 'Scope is "'+strScope+'"'});
		}
