x_snc_reactor_component.script:
class RIDECreateFileController extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		this.state = {
			app_file_classes: null,
			selected_class: null
		};
	}

	onSelect(strClassName)
	{
		this.setState(
			{
				selected_class: strClassName
			});
	}	

	onServerRequestGetAppFileClasses(response)
	{
		this.setState(
			{
				app_file_classes: response
			}
		);
	}		

	onServerRequestCreateAppFile(response)
	{
		// TODO: check if creating the file was successful
		//DoModalMessage('',response);
		this.onCreateFile()
	}

	onFileCreated()
	{
		//DoModalMessage('','Create File');
		/*
		if (IsFunction(this.props.callbackOnSelect) == true)
		{
			this.props.callbackOnSelect(strClassName);
		}
		*/

		if (IsFunction(this.props.callbackOnFileCreated) == true)
		{
			this.props.callbackOnFileCreated();
		}
	}	


	onCancel()
	{
		if (IsFunction(this.props.callbackOnCancel) == true)
		{
			this.props.callbackOnCancel();
		}
	}

	render()
	{
		var strAppSysId = this.props.app_sys_id;
		if (IsValidSysId(strAppSysId) == false)
		{
			return e(Centered,{},e(Error,{ text: 'No application specifified!' }));
		}

		var strSelectClass = GetStringValue(this.state.selected_class);
		if (strSelectClass != '')
		{
			ServerRequest(
				'x_snc_ride.RIDEClientAPI',
				'CreateAppFile',
				{},
				this.onServerRequestCreateAppFile);
			return e(Centered,{},e(Waiting,{ text: 'Creating Application File...' }));
		}


		var app_file_classes = this.state.app_file_classes;
		if (app_file_classes == null)
		{
			ServerRequest(
				'x_snc_ride.RIDEClientAPI',
				'GetAppFileClasses',
				{},
				this.onServerRequestGetAppFileClasses);
			return e(Centered,{},e(Waiting,{ text: 'Loading Application File Classes...' }));
		}

		var aRows = [];

		var aHeaders = [
			{
				header: true,
				content: 'Application File'
			},
			{
				header: true,
				content: 'Class Name'
			}
		];
		aRows.push(aHeaders);

		var aClassNames = GetKeys(app_file_classes);

		for (var nClass = 0; nClass < aClassNames.length; nClass++)
		{
			var strClassName = aClassNames[nClass];
			var app_file_class = app_file_classes[strClassName];
			var strName = GetValue(app_file_class,'name');

			var elementLinkName = e('a',
				{
					onClick: this.onSelect,
					value: strClassName
				},
				strName);

			var elementLinkClassName = e('a',
				{
					onClick: this.onSelect,
					value: strClassName
				},
				strClassName);				

			var aCells = [
				{ content: elementLinkName },
				{ content: elementLinkClassName }
			];


			aRows.push(aCells);
		}
		var table = e(TableComponent,{ cells: aRows, classname: 'ride_app_selector' },null);

		var cancelButton = e(Button,{ text: 'Cancel', callbackOnClick: this.onCancel});

		var aComponents = [];
		aComponents.push(e('div', { style: { maxHeight: '80vh', overflowY: 'auto' }},table));
		aComponents.push(e('div', { style: { float: 'right', marginTop: '10px'}},cancelButton));

		//return e('div',{},aComponents);
		return e(Centered,{},aComponents);
	}	
}
