x_snc_reactor_component.script:
class RIDEAppSelectorController extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		this.state = {
			apps: null
		};
		this.onServerRequestGetApps = this.onServerRequestGetApps.bind(this);
		this.onClickSelectApp = this.onClickSelectApp.bind(this);
		this.onClickCancel = this.onClickCancel.bind(this);
	}

	onServerRequestGetApps(response)
	{
		Log('onServerRequestGetApps');
		this.setState(
			{
				apps: response
			}
		);
	}	

	onClickSelectApp()
	{
		var strAppSysId = GetStringValue(event.target.attributes.value.value);
		if (typeof this.props.callbackSelectApp == 'function')
		{
			this.props.callbackSelectApp(strAppSysId);
		}	
	}

	onClickCancel()
	{
		if (typeof this.props.callbackSelectApp == 'function')
		{
			this.props.callbackSelectApp(this.props.app_id);
		}			
	}

	render()
	{
		var aApps = GetArrayValue(this.state.apps);
		if (aApps.length == 0)
		{
			ServerRequest('x_snc_ride.RIDEClientAPI','GetApps',{ },this.onServerRequestGetApps);
			return e(Centered,{},e(Waiting,{ text: 'Loading Apps...' }));
		}

		var aRows = [];

		var aHeaders = [
			{
				header: true,
				content: 'Name'
			},
			{
				header: true,
				content: 'Scope'
			}
		];
		aRows.push(aHeaders);

		for (var nApp = 0; nApp < aApps.length; nApp++)
		{
			var app = aApps[nApp];
			var elementLink = e('a',
				{
					onClick: this.onClickSelectApp,
					value: app.sys_id
				},
				app.name_clean + ' ' + app.version);

			var aCells = [
				{ content: elementLink },
				{ content: app.scope }
			];


			aRows.push(aCells);
		}
		var table = e(TableComponent,{ cells: aRows, classname: 'ide_app_selector' },null);

		var cancelButton = null;
		var strAppId = GetStringValue(this.props.app_id);
		if (strAppId != '')
		{
			cancelButton = e(Button,{ text: 'Cancel', callbackOnClick: this.onClickCancel});
		}

		var aComponents = [];
		aComponents.push(table);
		if (cancelButton != null)
		{
			aComponents.push(e('div', { style: { float: 'right', marginTop: '10px'}},cancelButton));
		}

		return e('div',{},aComponents);
	}	
}
